# Day 2 of Advent Of Code 2016

$actual = true

if $actual
  NOTE = [
    '',
    '',
    '',
    '',
    ''
  ]
else
  NOTE = "ULL\nRRDDD\nLURDL\nUUUUD".split "\n"
end

$last = 5
$code = Array.new

# u, d, l, r functions
FNS = {
  'U' => -3,
  'D' => 3,
  'L' => -1,
  'R' => 1
}

NOTE.each { |char|
  char.each_char { |ins|
    newloc = $last + FNS[ins]
    if newloc > 0 and newloc < 10
      $last = newloc
    end
  }
  $code.push $last
}

puts "code: #{$code.join ''}"
